
public class AssignemtIncrementOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=30;
		int c;
		c=a; 
		System.out.println(c);
		c=b;
		System.out.println(c);
		c=10;
		c++;
		System.out.println(c);
		c--;
		System.out.println(c);
		c+=5;
		System.out.println(c);
		c-=5;
		System.out.println(c);







	}

}
