package ConditionalStatements;

public class ElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day=4;
		if(day==1)
		{
			System.out.println("sunday");

		}
		else if(day==2)
		{
			System.out.println("monday");

		}
		else
		{
			System.out.println("rest of the days");

		}

	}

}
